import React, { useState, useEffect } from "react";
import { useLocation } from 'react-router-dom';
import { parseInput } from "./hexUtils";
import { decodeProto, decodeVarint } from "./protobufDecoder";

function App() {
  const [response, setResponse] = useState(null);
  const location = useLocation();

  useEffect(() => {
    if (location.pathname !== '/') {
      const newHex = decodeURI(location.pathname.substr(1));
      const buffer = parseInput(newHex);
      const decodeResult = decodeProto(buffer);
      const firstValueBuffer = decodeResult.parts[0].value;
      const firstValue = decodeVarint(firstValueBuffer, 0).value; // decode the buffer
      setResponse({ value: firstValue.toString() }); // '.toString()' is used to convert the BigInt returned from `decodeVarint` to a string.
    }
  }, [location.pathname]);

  return response ? (
    <div>{JSON.stringify(response)}</div>
  ) : null;
}

export default App;
