        setHex(json.value);
        setHexBuffer(json.value);
      });
  }
} , [location.pathname]);


  const result = hexBuffer ? (


      <ProtobufDisplay value={decodeProto(hexBuffer)} />

  ) : null;

  return (
    <Fragment>



      {result}

    </Fragment>
  );
}

export default App;
